import createComponent from 'utils/test/create-component';
import renderedMarkup from 'utils/test/rendered-markup';
import React from 'react/addons';

import Video from 'components/video';
import variations from 'components/video/.variations';

const data = variations[0].data;

import { expect } from 'chai';

describe('Video', function() {

  var video, renderedVideo;

  beforeEach(function() {
    video = createComponent(Video, data);
    //console.log('*************',video);

    renderedVideo = React.renderToString(React.createElement(Video, data));
    //console.log('#############',renderedVideo);
  });

  describe('renders', function() {

    // @QUESTION: do we test the React object or the markup string?
    it('a div tag', function() {
      expect(renderedVideo).to.contain('<div');
    });

    it('a div tag at the root', function() {
      expect(video.type).to.equal('div');
    });
  });
});
