import React from 'react';
import BEM from 'react-bem-helper';
import path from 'path';

import Icon from 'components/icon';
import Section from 'components/section';
import Container from 'components/container';

const classes = new BEM('weather-forecast-list');

export default React.createClass({

  displayName: 'WeatherForecastList',

  propTypes: {
    cities: React.PropTypes.array.isRequired,
    forecast: React.PropTypes.object.isRequired
  },

  render() {

    var prefix = process.env.STATIC_ASSETS;
    var href = path.join(prefix, 'images/weather/color/55.svg');

    return (
      <Section modifier='default'>
        <Container {...classes()}>
          <table {...classes('table')}>
            <tbody>
              <tr>
                <td {...classes('time')}>00:00</td>
                <td><img {...classes('img')} src={href} /></td>
                <td {...classes('test')}>
                  <span {...classes('text')}>wechselnd bewölkt</span>
                  <span {...classes('temp')}>24°</span>
                </td>
                <td {...classes('wind')}><Icon id="arrow-circle" /> 13km/h</td>
              </tr>
              <tr>
                <td {...classes('time')}>00:00</td>
                <td><img {...classes('img')} src={href} /></td>
                <td>
                  <span {...classes('text')}>wechselnd bewölkt</span>
                  <span {...classes('temp')}>24°</span>
                </td>
                <td {...classes('wind')}><Icon id="arrow-circle" /> 13km/h</td>
              </tr>
            </tbody>
          </table>
        </Container>
      </Section>
    );
  }
});
