import React from 'react';
import BEM from 'react-bem-helper';

import Icon from 'components/icon';

const classes = new BEM({
  name: 'weather-forecast',
  prefix: ''
});

export default React.createClass({

  displayName: 'WeatherForecast',

  propTypes: {
    weatherIcon: React.PropTypes.string.isRequired,
    minTemperature: React.PropTypes.number.isRequired,
    maxTemperature: React.PropTypes.number.isRequired
  },

  render() {

    return (
      <div {...classes('')}>
        <div {...classes('icon')}>
          <Icon id={this.props.weatherIcon} />
        </div>
        <div {...classes('temperature')}>
          <div {...classes('max')}>{this.props.maxTemperature}°</div>
          <div {...classes('min')}>{this.props.minTemperature}°</div>
        </div>
      </div>
    );

  }
});
