/* eslint-disable no-console */
var loadCities = require('../../../../server/api/loadCities');
var loadWeather = require('../../../../server/api/loadWeather');

module.exports = function(req, res) {
  console.info('============api.js=============');

  // load Cities data
  loadCities(req, res);

  // load Weather data of selected parish
  loadWeather(req, res);


  // signal that we need to wait for the API to finish the request
  return true;
};
