import React from 'react';
import bem from 'react-bem-helper';
import Header from './header';
import Sidebar from './sidebar';
import isLoggedIn from 'helpers/dashboard/is-logged-in';

const classes = new bem('dashboard');

export default React.createClass({

  displayName: 'DashBoardLayout',

  render() {
    const isUserLoggedIn = isLoggedIn();
    return (
      <div {...classes(null, isUserLoggedIn ? 'logged-in' : 'logged-out')} >
        <Header loggedIn={isUserLoggedIn} />
        { isUserLoggedIn ? <Sidebar currentPath={ this.props.location.pathname } /> : null }
        <div {...classes('page')}>
          {this.props.children}
        </div>
      </div>
    );
  }

});
