import React from 'react';
import bem from 'react-bem-helper';
import cookie from 'cookie';
import Authentication from 'dashboard/Authentication';

const classes = new bem('dashboard');

export default React.createClass({

  displayName: 'DashBoardLayout',

  getInitialState() {
    return {
      isLoggedIn: this.isLoggedIn()
    }
  },

  isLoggedIn() {
    var cookies = cookie.parse(document.cookie);
    return !!(cookies && cookies.azrsession);
  },

  setLoggedIn() {
    this.setState({ isLoggedIn: true });
  },

  render() {
    return (
      <div>
        { this.state.isLoggedIn ? null : <Authentication onSuccess={this.setLoggedIn}/> }
      </div>
    )
  }

});
