import _ from 'lodash';
import capitalizeAll from 'helpers/string/capitalize-all';

export default function(source) {
  if (source) {
    let sourceString = source.title || _.get(source, 'source.title') || '';
    let spaceSlashes = sourceString.replace(/\//g, ' \/ ');
    return capitalizeAll(spaceSlashes);
  }
}
