import _ from 'lodash';
import getPrimaryDossierData from 'helpers/dossier/get-primary-data';

const removedBackgroundsByVariation = [
  'dossier-lite'
];

export default function(dossier, variation) {
  let primaryProps = getPrimaryDossierData(dossier);

  let additionalProps = {
    variation: variation,
    catchword: "Dossier",
    linkText: "zum Dossier"
  };

  if (_.includes(removedBackgroundsByVariation, variation)) {
    additionalProps.background = false;
  }

  return _.merge({}, primaryProps, additionalProps);
}
