import _ from 'lodash';
import path from 'path';
import getImageProps from 'helpers/asset/get-image-props';

export default function(dossier) {
  let header = dossier.header;
  let mainAsset = getImageProps(_.first(header.assets));
  return {
    title: dossier.title,
    link: path.join('dossier', _.get(dossier, 'urlpart')),
    background: mainAsset ? mainAsset.src : null,
    image: mainAsset,
  };
}
