export default function(wrapper) {
  const img = wrapper.querySelector('img');
  const wrapperDims = wrapper.getBoundingClientRect();
  const imgDims = img.getBoundingClientRect();
  const isLandscape = imgDims.width > imgDims.height;
  const resizeX = Math.floor(wrapperDims.width) > imgDims.width;
  const resizeY = Math.floor(wrapperDims.height) > imgDims.height;
  const comparison = resizeX ? 'width' : 'height';
  const percentageLarger = (wrapperDims[comparison] / imgDims[comparison] * 100);

  // landscape images
  if (isLandscape) {
    if (resizeX) {
      img.style.width = percentageLarger + '%';
    } else if (resizeY) {
      img.style.width = percentageLarger + '%';
      img.style.marginLeft = -((percentageLarger - 100) / 2) + '%';
    }
  }
  // portrait images
  if (resizeY && !isLandscape) img.style.height = percentageLarger + '%';
}
