import _ from 'lodash';

// Map icon id based on labeltype
const icons = {
  video_autoplay: 'play',
  publi_reportage: 'star'
};

// Map text based on labeltype
const texts = {
  publi_reportage: 'Publireportage'
};

export default function(article) {
  let labelId = _.get(article, 'labeltype');
  return {
    icon: _.get(icons, labelId),
    text: _.get(texts, labelId)
  };
}
