import _ from 'lodash';

// Map title icons based on teaser labeltype
const titleIcons = {
  video_autoplay: 'videoAuto',
  video: 'video',
  quiz: 'quiz',
  survey: 'survey',
  map: 'map',
  leserbeitrag: 'microphone',
  vereinsmeldung: 'microphone',
  sportverein: 'microphone',
  gemeindebeitrag: 'microphone'
};

export default function(article) {
  let labelType = _.get(article, 'labeltype');
  return {
    text: _.get(article, 'title'),
    icon: _.get(titleIcons, labelType)
  };
}
