import stripMarkup from 'helpers/strip-markup';
import getSource from 'helpers/get-source';

export default function(asset) {
  if (asset) {

    let strippedDescription = asset.description
      ? stripMarkup(asset.description)
      : stripMarkup(asset.asset.description);

    return {
      title: asset.title || asset.asset.title,
      description: strippedDescription,
      source: asset.asset.source ? getSource(asset.asset.source) : null
    };

  }
}
