import _ from 'lodash';

let allowedMainAssets = [
  'asset_image',
  'asset_video',
  'asset_image_gallery'
];

export default function(assets) {
  if (assets) {

    let mainAsset = _.find(assets, function(_asset) {
      return _asset.asset && _.includes(allowedMainAssets, _asset.asset.content_type);
    });

    if (mainAsset) {
      return mainAsset;
    }

    return null;

  }
}
