import skins from 'config/skins.json';
import _ from 'lodash';

/**
 * resolve the skin name from a domain string
 *
 * @param  {String}  domain  The domain to check
 *
 * @return  {String}         The skin name or undefined if not found
 */
function getSkinFromDomain (domain) {
  var result = _.findKey(skins, function (item) {
    return item === domain;
  });
  return result;
}

export default getSkinFromDomain;
