import capitalizeAll from 'helpers/capitalize-all';

export default function(source, addCopyright) {
  if (source) {
    let sourceString = source.title || (source.source ? source.source.title : '');
    let cleanSource = capitalizeAll(sourceString.replace('/', ' / '));
    if (cleanSource && addCopyright) cleanSource = 'Â© ' + cleanSource;
    return cleanSource;
  }
}
