// Map image sizes based on teaser variation
const imageSizes = {
  'basic': 'small',
  'basic-listed': 'small',
  'large': 'large',
  'inlined-right': 'tiny',
  'inlined-left': 'tiny'
};

// Map image ratios based on teaser variation
const imageRatios = {
  'basic': '16x9',
  'basic-listed': '16x9',
  'large': '16x9',
  'inlined-right': '1x1',
  'inlined-left': '1x1'
};

// Map fallbacks
const imageFallbacks = {
  'basic': 'light',
  'basic-listed': 'light',
  'large': 'light'
};

// Map date formats based on teaser variation
const dateFormats = {
  'basic': 'long',
  'basic-listed': 'long',
  'light': 'long',
  'light-boxed': 'long',
  'listed': 'listed',
  'inlined-right': 'short',
  'inlined-left': 'short'
};

export default function(variation) {
  return {
    imageSize: imageSizes[variation],
    imageRatio: imageRatios[variation],
    imageFallback: imageFallbacks[variation],
    dateFormat: dateFormats[variation]
  };
}
