import _ from 'lodash';

// Map title icons based on teaser labeltype
const titleIcons = {
  quiz: 'quiz',
  survey: 'survey',
  map: 'map',
  video: 'video',
  // 'publi_reportage': 'star',
  leserbeitrag: 'microphone',
  vereinsmeldung: 'microphone',
  sportverein: 'microphone',
  gemeindebeitrag: 'microphone'
};

export default function(label) {
  return {
    titleIcon: _.get(titleIcons, label),
  };
}
