import _ from 'lodash';

const config = {
  'basic-listed': {
    mobile: 'inlined-left',
    desktop: 'basic'
  },
  'summary-listed': {
    mobile: 'listed',
    desktop: 'summary'
  }
};

export default function(variation, viewport) {
  let find = variation + '.' + viewport;
  return _.get(config, find) || variation;
}
