import _ from 'lodash';
import { getTeaserSwitchableFromArticle } from 'helpers/teaser';

export default function(data) {
  if (data) {

    let mappedTeasers = function(articles) {
      return articles.map((article) => {
        return getTeaserSwitchableFromArticle({
          article: article,
          variation: 'inlined-left-to-basic'
        });
      });
    };

    return _.assign({}, data, { articles: mappedTeasers(data.articles) });
  }
}
