/**
 * Checks if user agent is of a known mobile OS
 * TODO: might be better done by feature detection
 *
 * @return  {Boolean}  True if mobile otherwise false
 */
export default function isMobile() {
  // check user agent to determine if we are on a mobile browser
  var uaRegex = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i;
  return (uaRegex.test(navigator.userAgent));
}
