export default class KalturaImageProxy {
  constructor(accountID='1719221', ssl=true, videoID, quality=30) {
    this.accountID = accountID;
    this.ssl = ssl;
    this.videoID = videoID;
    this.quality = quality;
  }

  getBaseURL() {
    var urlRoot = this.ssl ? 'https://cdnapisec.kaltura.com/' : 'http://cdn.kaltura.com/';
    return urlRoot
            + 'p/'
            + this.accountID
            + '/thumbnail/entry_id/'
            + this.videoID
            + '/quality/' + this.quality + '/';
  }

  getImageURL(width, height, crop) {
    var url = this.getBaseURL();
    return url
          + 'width/' + width + '/'
          + 'height/' + height + '/'
          + 'type/' + (crop ? 3 : 1);
  }

}
