// Helper functions for handling MIME types

var mimeTypes = {
  'application/pdf': 'pdf',
  'application/msword': 'doc',
  'application/msexcel': 'xls',
  'application/mspowerpoint': 'ppt',
};


/**
 * get the file extension from a MIME type
 *
 * @param  {string}  mimeType  a mime type provided by the CMS API
 *
 * @return  {string}  file extension of the MIME type
 *                    or empty string if not found
 */
var getFileExtension = function(mimeType) {
  var fileExtension = '';

  fileExtension = mimeTypes[mimeType] ? mimeTypes[mimeType] : '';
  return fileExtension;
};

export default {
  getFileExtension: getFileExtension
};
