/* eslint-disable no-unused-expressions */

import { getDomainInfo } from 'helpers';
import _ from 'lodash';

import { expect } from 'chai';

describe('Helpers', () => {

  describe('getDomainInfo', () => {

    it('returns object for skin', () => {
      let result = getDomainInfo('blz');
      expect(result).to.be.an('object');
    });

    it('returns all data for aaz', () => {
      let result = getDomainInfo('aaz');
      expect(_.has(result, 'href')).to.be.true;
      expect(_.has(result, 'label')).to.be.true;
      expect(_.has(result, 'logo')).to.be.true;
      expect(_.has(result, 'logoMobile')).to.be.true;
      expect(_.has(result, 'organisation')).to.be.true;
      expect(_.has(result, 'domain')).to.be.true;
    });

    it('returns an empty object when not found', () => {
      let result = getDomainInfo('fallback');
      expect(result).to.be.an('object');
    });

  });

});
