/* eslint-disable no-unused-expressions */

import getEnrichmentPlacement from 'helpers/enrichment/get-placement';

import { expect } from 'chai';

describe('Helpers', () => {

  describe('getEnrichmentPlacement', () => {

    it('returns aside for left', () => {
      expect(getEnrichmentPlacement('left')).to.equal('aside');
    });

    it('returns aside for right', () => {
      expect(getEnrichmentPlacement('right')).to.equal('aside');
    });

    it('returns full everything else', () => {
      expect(getEnrichmentPlacement('max')).to.equal('full');
      expect(getEnrichmentPlacement('full')).to.equal('full');
      expect(getEnrichmentPlacement('test')).to.equal('full');
      expect(getEnrichmentPlacement('')).to.equal('full');
    });

  });

});
