/* eslint-disable no-unused-expressions */

import _ from 'lodash';

import getEnrichmentType from 'helpers/get-enrichment-type';
import article from 'utils/data/master-article';

import { expect } from 'chai';

describe('Helpers', function() {

  describe('getEnrichmentType', function() {

    let assetFixture, stoererFixture;

    beforeEach(function() {
      assetFixture = _.first(article.article.assets);
      stoererFixture = _.first(article.article.stoerer);
    });

    it('returns for assets', function() {
      expect(getEnrichmentType(assetFixture)).to.equal('asset_document');
    });

    it('returns for stoerers', function() {
      expect(getEnrichmentType(stoererFixture)).to.equal('text_stoerer');
    });

    it('returns empty for fallback', function() {
      expect(getEnrichmentType({})).to.equal('');
    });

  });

});
