/* eslint-disable no-unused-expressions */

import getFileExtension from 'helpers/get-file-extension';

import { expect } from 'chai';

describe('Helpers', () => {

  describe('getFileExtension', () => {

    it('returns for .pdf', () => {
      expect(getFileExtension('application/pdf')).to.equal('pdf');
    });

    it('returns for .doc', () => {
      expect(getFileExtension('application/msword')).to.equal('doc');
    });

    it('returns for .xls', () => {
      expect(getFileExtension('application/msexcel')).to.equal('xls');
    });

    it('returns for .ppt', () => {
      expect(getFileExtension('application/mspowerpoint')).to.equal('ppt');
    });

    it('returns empty if not found', () => {
      expect(getFileExtension('application/woff2')).to.equal('');
    });

  });

});
