import { getScript } from 'helpers';
import { expect } from 'chai';

describe('Helpers', () => {

  describe('getScript', () => {

    it('should return a promise', () => {
      var url = 'foo.js';
      var promise = getScript(url);
      expect(promise.then).to.be.a('function');
      expect(promise.catch).to.be.a('function');
    });

  });

});
