import { getSkinFromDomain } from 'helpers';
import { expect } from 'chai';

describe('Helpers', () => {

  describe('getSkinFromDomain', () => {

    it('returns skin name from url', () => {
      let result = getSkinFromDomain('basellandschaftlichezeitung.ch');
      expect(result).to.equal('blz');
    });

    it('returns skin name from url with www.', () => {
      let result = getSkinFromDomain('www.basellandschaftlichezeitung.ch');
      expect(result).to.equal('blz');
    });

    it('returns null when domain isnt present', () => {
      let result = getSkinFromDomain('www.google.com');
      expect(result).to.equal(null);
    });

    it('returns null when empty', () => {
      let result = getSkinFromDomain('');
      expect(result).to.equal(null);
    });

  });

});
