/* eslint-disable no-unused-expressions */

import stripMarkup from 'helpers/strip-markup';

import { expect } from 'chai';

describe('Helpers', function() {

  describe('stripMarkup', function() {

    it('strips markup correctly', function() {
      let fixture = '<p>Grenchener Tagblatt <h2>Grenchener Tagblatt</h3> Grenchener Tagblatt</a></a></p>';
      expect(stripMarkup(fixture)).to.equal('Grenchener Tagblatt Grenchener Tagblatt Grenchener Tagblatt');
    });

    it('works without text content', function() {
      let fixture = '<p></p><h3><ul>';
      expect(stripMarkup(fixture)).to.equal('');
    });

  });

});
