/* eslint-disable no-unused-expressions */

import stripMarkup from 'helpers/string/strip-markup';

import { expect } from 'chai';

describe('Helpers', () => {

  describe('stripMarkup', () => {

    it('strips markup correctly', () => {
      let fixture = '<p>Grenchener Tagblatt <h2>Grenchener Tagblatt</h3> Grenchener Tagblatt</a></a></p>';
      let result = stripMarkup(fixture);
      expect(result).to.equal('Grenchener Tagblatt Grenchener Tagblatt Grenchener Tagblatt');
    });

    it('works without text content', () => {
      let fixture = '<p></p><h3><ul>';
      let result = stripMarkup(fixture);
      expect(result).to.equal('');
    });

  });

});
