/**
 * Strips all <p> tags from a text string
 *
 * @param  {string}  string  String to be processed
 *
 * @return  {string}  The cleaned string or empty string (if no input)
 */
const stripParagraphs = (string) => {
  if (!string && !string.length) {
    return '';
  }
  return string.replace(/(<p[^>]+?>|<p>|<\/p>)/img, ' ');
};

export default stripParagraphs;
