import _ from 'lodash';

const config = {
  imageSize: {
    'basic': {
      size: 'small',
      ratio: '16x9'
    },
    'summary': {
      size: 'small',
      ratio: '16x9'
    },
    'large': {
      size: 'large',
      ratio: '16x9'
    },
    'inlined-left': {
      size: 'tiny',
      ratio: '1x1'
    },
    'inlined-right': {
      size: 'tiny',
      ratio: '1x1'
    },
    'mega': {
      size: 'full',
      ratio: '9x4'
    },
    'quote': {
      size: 'tiny',
      ratio: '1x1'
    }
  },
  dateFormat: {
    'basic': 'long',
    'summary': 'long',
    'light': 'long',
    'light-boxed': 'long',
    'listed': 'listed',
    'inlined-right': 'short',
    'inlined-left': 'short',
  }
};

export default function(element, variation) {
  let find = element + '.' + variation;
  return _.get(config, find);
}
