import {
  getArticleTitle,
  getArticleUrl,
  getArticleImage,
  getArticleMarker,
  getArticleSchema,
  getArticleTheme,
  getArticleByline,
  getArticleSummary,
  getArticleCatchword
} from 'helpers/article';

import getTeaserVariation from './get-variation';

export default function(data) {

  if (!data) {
    return false;
  }

  const { article, variation } = data;

  return {
    title: getArticleTitle(article),
    link: getArticleUrl(article),
    marker: getArticleMarker(article),
    schema: getArticleSchema(article),
    catchword: getArticleCatchword(article),
    // props which change based on teaser variation
    variation: getTeaserVariation(article, variation),
    theme: getArticleTheme(article, variation),
    image: getArticleImage(article, variation),
    byline: getArticleByline(article, variation),
    summary: getArticleSummary(article, variation)
  };
}
