import _ from 'lodash';

const config = {
  'basic-listed': {
    small: 'inlined-left',
    large: 'basic',
    breakpoint: 500
  },
  'summary-listed': {
    small: 'listed',
    large: 'summary',
    breakpoint: 500
  }
};

export default function(variation) {
  return _.get(config, variation);
}
