import _ from 'lodash';

import variationConfig from './get-variation-config';

export default function(props, state) {

  let variation = _.get(props.responsive, state.view);
  let config = variationConfig(variation);

  return {
    variation: variation,
    image: config.image
      ? _.merge({}, props.image, config.image)
      : false,
    byline: config.byline
      ? _.merge({}, props.byline, config.byline)
      : false,
    summary: config.summary
      ? props.summary
      : false
  };
}
