import getVariation from './get-variation';

export default function(article) {
  return {
    'inlined-left-to-basic': {
      small: getVariation(article, 'inlined-left'),
      large: getVariation(article, 'basic'),
      breakpoint: 500
    },
    'listed-to-summary': {
      small: getVariation(article, 'listed'),
      large: getVariation(article, 'summary'),
      breakpoint: 500
    }
  };
}
