import { expect } from 'chai';

import markers from 'higher-order/body-parser/lib/markers';
import findEnrichmentMarkers from 'higher-order/body-parser/lib/find-enrichment-markers';

describe('Body Parsing Regular Expression', function() {

  describe('returns', function() {
    it('for plain stoerer marker', function() {
      let text =  markers.enrichment;
      expect(findEnrichmentMarkers(text)).to.have.length.above(1);
    });

    it('for plain empfehlung marker', function() {
      let text =  markers.related;
      expect(findEnrichmentMarkers(text)).to.have.length.above(1);
    });

    it('for adjacent stoerer markers', function() {
      let text =  markers.enrichment + markers.enrichment;
      expect(findEnrichmentMarkers(text)).to.have.length.above(1);
    });

    it('for adjacent empfehlung markers', function() {
      let text =  markers.related + markers.related;
      expect(findEnrichmentMarkers(text)).to.have.length.above(1);
    });

    it('for adjacent stoerer / empfehlung markers', function() {
      let text =  markers.enrichment + markers.related;
      expect(findEnrichmentMarkers(text)).to.have.length.above(1);
    });

    it('for adjacent empfehlung / stoerer markers', function() {
      let text =  markers.related + markers.enrichment;
      expect(findEnrichmentMarkers(text)).to.have.length.above(1);
    });
  });
});
