import React from 'react';

export default Component => React.createClass({

  mappedData() {
    var props = this.props;

    return {
      catchword: props.context_label,
      title: props.title,
      author: props.author,
      source: props.source.title,
      published: props.dc.effective,
      modified: props.dc.modified
    };
  },

  render() {
    return (
      <Component {...this.mappedData()} />
    );
  }

});
