import React from 'react';

export default Component => React.createClass({

  displayName: 'Demo',

  mappedData() {
    var props = this.props;

    return {
      title: props.title,
      context_label: props.context_label,
      lead: props.lead,
      comment_count: props.comment_count
    };
  },

  render() {
    return (
      <Component {...this.mappedData()} />
    );
  }

});
