var escapeHtml = require('escape-html');

module.exports = function() {

  var render = function(component, state, meta) {
    var html;
    html =  '<div class="az-iso" data-meta="' + escapeHtml(JSON.stringify(meta)) + '">';
    html += '<div class="az-iso-component">';
    html += component;
    html += '</div>';
    html += '<span aria-hidden="true" class="az-iso-state" data-state="' + escapeHtml(JSON.stringify(state)) + '"></span>';
    html += '</div>';

    return html;
  };

  return Object.freeze({
    render: render
  });
};
