import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem('input');

export default React.createClass({
  displayName: 'InputWrapper',

  propTypes: {
    label: React.PropTypes.string,
    id: React.PropTypes.string.isRequired,
    className: React.PropTypes.string,
    description: React.PropTypes.string,
    hint: React.PropTypes.oneOfType([
      React.PropTypes.string,
      React.PropTypes.element
    ])
  },

  render() {
    const className = [this.props.className, classes('wrapper').className].join(' ');
    const { id, label, description, children, hint } = this.props;

    return (
      <div className={className}>
        { label
          ? <label {...classes('label')} htmlFor={id}>{label}</label>
        : null }
        { description
          ? <p {...classes('description')}>{description}</p>
        : null }
        { children }
        { hint
          ? <div {...classes('hint')}>{hint}</div>
        : null }
      </div>
    );
  }
});



