// clarify: <div className="box anriss" onclick="javascript:window.location.href='${context/@@absolute_url}'">

//             {cut(view.spitzmarke, { cutLen: 300, softcut: true, fill: '...mehr' } )}
//            ${view.text.substring(0, 150)} ...mehr
//            ${view.text.substring(0, 300)} ...mehr
//            ${view.cut(view.text, { cutLen: 150, softcut: true, fill: '...mehr' } )}
//            ${view.cut(view.text, { cutLen: 300, softcut: true, fill: '...mehr' } )}

export default class ArticleAnriss extends React.Component {

  render() {

    const { view } = this.props;
    const absoluteUrl = view.getAbsoluteUrl();

    if (view.anriss) {
      return (
        <div dangerouslySetInnerHTML={{__html: `<div class="box anriss" onclick="javascript:window.location.href='${absoluteUrl}'">
          ${view.anriss}
        </div>`}} />
      );
    } else if (view.isUgcArticle) {
      if (view.providesTeaserCompatibleMainAsset) {
        return (
          <div dangerouslySetInnerHTML={{__html: `<div class="box anriss" onclick="javascript:window.location.href='${absoluteUrl}'">
            ${cut(view.text, { cutLen: 150, softcut: true, fill: '...mehr' } )}
          </div>`}} />
        );
      } else {
        return (
          <div dangerouslySetInnerHTML={{__html: `<div class="box anriss" onclick="javascript:window.location.href='${absoluteUrl}'">
            ${cut(view.text, { cutLen: 300, softcut: true, fill: '...mehr' } )}
          </div>`}} />
        );
      }
    }

    // might happen ...
    return null;

  }
}
