//<div className="box anriss" onclick="javascript:window.location.href='${context/@@absolute_url}'">

export default class ArticleAnriss extends React.Component {
  render() {

    const { view } = this.props;

    if (view.anriss) {
      return (
        <div className="box anriss" onclick="">
          {view.anriss}
        </div>
      );
    } else if (view.isUgcArticle) {
      if (view.providesTeaserCompatibleMainAsset) {
        return (
          <div className="box anriss" onclick="">
            {view.text.substring(0, 150) + "...mehr"}
          </div>
        );
      } else {
        return (
          <div className="box anriss" onclick="">
            {view.text.substring(0, 300) + "...mehr"}
          </div>
        );
      }
    }

    // might happen ...
    return null;

  }
}

