import React from 'react';
/*
<metal:macro define-macro="ArticleComments">
  <div class="commentscount" tal:condition="view/commentable">
    <a class="related-link"
       href=""
       tal:attributes="href string:${context/@@absolute_url}#comment-jumpto"
       tal:content="view/commentCount">
      Kommentare (0)
    </a>
  </div>
</metal:macro>
*/

export default class ArticleComments extends React.Component {

  render() {

    const { view } = this.props;

    if (view.commentable) {
      return (
        <div className="commentscount">
          <a className="related-link" href={view.getAbsoluteUrl() + '#comment-jumpto'}>
            {view.commentCount}
          </a>
        </div>
      );
    }

    return null;

  }
}
