export default class ArticleLabelTeaserNoDisplayAsset extends React.Component {

  render() {
    const { view } = this.props;

    if (view.mainAssetLabel || view.isUgcArticle) {
      let mainAssetLabel = null;
      if (view.mainAssetLabel) {
        mainAssetLabel = (
          <span className="articleLabel">
            <span>{view.mainAssetLabel}</span>
          </span>
        );
      }

      let articleLabelIcon = null;
      if (view.isUgcArticle) {
        articleLabelIcon = (
          <span className="articleLabelIcon" {...nbsp} />
        );
      }

      return (
        <div>
          <div className="articleLabelContainer">
            {mainAssetLabel}
            {articleLabelIcon}
          </div>
          <div className="clear" />
        </div>
      );
    }

    return null;
  }

}
