export default class Teaser9Col extends React.Component {

  constructor(props) {
    super(props);
    this.view = new View(this.props.article, { width: 608, height: 360 });
  }

  render() {
    const view = this.view;

    return (
      <div className="teaser teaser-9col">
        <ArticleLabel view={view} />
        <ArticleImage9Col view={view} />
        <div className="{view.teaserNineColCssClass}">
          <ArticleKeyword6Col view={view} />
          <ArticleHeadline6Col view={view} />
          <ArticleAnriss view={view} />
          <ArticleComments view={view} />
        </div>
      </div>
    );
  }

}
