import React from 'react';
import ArticleLabelTeaserNoDisplayAsset from "legacy/components/articleLabelTeaserNoDisplayAsset";
import ArticleKeyword3Col from "legacy/components/ArticleKeyword3Col";
import ArticleAnrissAsLink from "legacy/components/ArticleAnrissAsLink";
import ArticleComments from "legacy/components/ArticleComments";
import View from "legacy/helpers/views.jsx";

/*
<!-- teaser3Col just Anriss -->
<metal:macro define-macro="teaserJustAnriss3Col">
<div class="teaser teaser-3col">
  <metal:macro use-macro="context/@@ArticleMacros/articleLabelTeaserNoDisplayAsset" />
  <metal:macro use-macro="context/@@ArticleMacros/ArticleKeyword3Col" />
  <metal:macro use-macro="context/@@ArticleMacros/ArticleAnrissAsLink" />
  <metal:macro use-macro="context/@@ArticleMacros/ArticleComments" />
</div>
</metal:macro>
*/

export default class TeaserJustAnriss3Col extends React.Component {

  constructor(props) {
    super(props);
    this.view = new View(this.props.article, { width: 220, height: 130 });
  }

  render() {
    const view = this.view;

    return (
      <div className="teaser teaser-3col">
        <ArticleLabelTeaserNoDisplayAsset view={view} />
        <ArticleKeyword3Col view={view} />
        <ArticleAnrissAsLink view={view} />
        <ArticleComments view={view} />
      </div>
    );
  }

}
