import React from 'react';
import ArticleLabel from "legacy/components/articleLabel";
import ArticleImage6Col from "legacy/components/articleImage6Col";
import ArticleKeyword6Col from "legacy/components/ArticleKeyword6Col";
import ArticleHeadline6Col from "legacy/components/ArticleHeadline6Col";
import ArticleAnriss from "legacy/components/ArticleAnriss";
import ArticleComments from "legacy/components/ArticleComments";
import View from "legacy/helpers/views.jsx";

/*
<!-- teaser6Col no Anriss -->
<metal:macro define-macro="teaserNoAnriss6Col">
<div class="teaser teaser-6col">
  <metal:macro use-macro="context/@@ArticleMacros/articleLabel" />
    <tal:cond  tal:condition="view/providesTeaserCompatibleMainAsset">
      <metal:macro use-macro="context/@@ArticleMacros/articleImage6Col" />
      <metal:macro use-macro="context/@@ArticleMacros/ArticleKeyword6Col" />
      <metal:macro use-macro="context/@@ArticleMacros/articleHeadline6Col" />
    </tal:cond>
    <tal:else tal:condition="not:view/providesTeaserCompatibleMainAsset">
      <metal:macro use-macro="context/@@ArticleMacros/ArticleKeyword6Col" />
      <metal:macro use-macro="context/@@ArticleMacros/articleHeadline6Col" />
      <metal:macro use-macro="context/@@ArticleMacros/ArticleAnriss" />
    </tal:else>
    <metal:macro use-macro="context/@@ArticleMacros/ArticleComments" />
</div>
</metal:macro>
*/

export default class TeaserNoAnriss6Col extends React.Component {

  constructor(props) {
    super(props);
    this.view = new View(this.props.article, { width: 460, height: 272 });
  }

  render() {
    const view = this.view;

    return (
      <div className="teaser teaser-6col">
        <ArticleLabel view={view} />
        {
          (view.providesTeaserCompatibleMainAsset)
          ? <ArticleImage6Col view={view} />
          : null
        }
        <ArticleKeyword6Col view={view} />
        <ArticleHeadline6Col view={view} />
        {
          (!view.providesTeaserCompatibleMainAsset)
          ? <ArticleAnriss view={view} />
          : null
        }
        <ArticleComments view={view} />
      </div>
    );
  }

}
