export default class TeaserNoImage6Col extends React.Component {

  constructor(props) {
    super(props);
    this.view = new View(this.props.article, { width: 460, height: 272 });
  }

  render() {
    const view = this.view;

    return (
      <div className="teaser teaser-6col teaser-6col-noimage">
        <ArticleLabelTeaserNoDisplayAsset view={view} />
        <ArticleKeyword6Col view={view} />
        <ArticleHeadline6Col view={view} />
        <ArticleAnriss view={view} />
        <ArticleComments view={view} />
      </div>
    );
  }

}
